// Session 2 - Control Structures and Flow

// 01 - Conditional statements

// Change the values and operators below to test your algorithm meets all conditions
var x = 50;
var expression1 = (x < 25);
var expression2 = (x > 50);

// Write Your JavaScript Code Here
// Check if both expressions are true using &&

if (expression1 && expression2) {
    console.log("True ✅ True ✅");

// If both conditions are not true, check if expression1 is true
} else if (expression1) {
    console.log("True ✅ False ❌");

// If expression1 is not true, then check if expression2 is true
} else if (expression2) {
    console.log("False ❌ True ✅");

// If none of the conditions above evaluate to true, both expressions must be false
} else {
    console.log("False ❌ False ❌");
}

// switch statements

/*

switch statements evaluates an expression against a series of case clauses.
it executes a statement after the first case clause with a matching value until a break is encountered.
default clause will trigger if no case matches the expression value

*/

// The getDay() method returns the weekday as a number between 0 and 6.
let day;

switch (new Date().getDay()) {
  case 0:
    day = "Sunday";
    break;
  case 1:
    day = "Monday";
    break;
  case 2:
     day = "Tuesday";
    break;
  case 3:
    day = "Wednesday";
    break;
  case 4:
    day = "Thursday";
    break;
  case 5:
    day = "Friday";
    break;
  case 6:
    day = "Saturday";
}

console.log(day)


// 02 - Loops

// We use a for-loop to execute code more than once
for (var i = 0; i < 5; i++) {
  // This is the block of code that will run each time
  console.log('This is the current value of i: ' + i + '.');
}

// For-loops are often used to iterate over arrays
var fruits = ['Apples', 'Bananas', 'Grapes', 'Peaches', 'Pears'];

// To determine how many times the loop should execute, we use the array's length
for (var i = 0; i < fruits.length; i++) {
  console.log('I am going to the market for ' + fruits[i] + '.');
}


// 03 - Break and Continue statements

// empty array
var fruitsWeBought = []

for (var j = 0; j < fruits.length; j++) {
  var fruit = fruits[j]

  // we didn't buy Bananas so we want to skip it but still remain in our loop
  if (fruit === 'Bananas') {
  // continue statement terminates current iteration of current loop and moves on to next
    continue  
  }

  // our cart is full and we can't buy anymore fruit, so we want to exit our loop
  if (fruit === 'Peaches') {

    // terminates current loop
    console.log(fruit + ' === Peaches so break loop')
    break
  }

 console.log("iteration: " + j + " = " + fruit)
 // fill the new array with fruit we bought
 fruitsWeBought.push(fruit)
}

console.table(fruitsWeBought)

